/*Table structure for table `manager_org_apply` */

CREATE TABLE `manager_org_apply` (
  `org_apply_id` varchar(32) NOT NULL COMMENT '机构入驻申请表Id',
  `user_name` varchar(50) NOT NULL COMMENT '申请人账号',
  `apply_name` varchar(50) DEFAULT NULL COMMENT '申请人姓名',
  `phone` varchar(20) DEFAULT NULL COMMENT '手机',
  `email` varchar(50) DEFAULT NULL COMMENT 'Email',
  `organization_pic` varchar(2000) DEFAULT NULL COMMENT '机构图标',
  `organization_name` varchar(100) DEFAULT NULL COMMENT '机构名称',
  `organization_description` varchar(200) DEFAULT NULL COMMENT '机构简介',
  `organization_brief` varchar(100) DEFAULT NULL COMMENT '一句话介绍',
  `main_category` int(11) DEFAULT NULL COMMENT '主营类目',
  `commitment_pic` varchar(2000) DEFAULT NULL COMMENT '承诺书图片',
  `organization_nature` int(11) DEFAULT NULL COMMENT '机构性质（1 有营业执照；2 没有营业执照）',
  `license_pic` varchar(2000) DEFAULT NULL COMMENT '营业执照/教育资质证书',
  `number` varchar(200) DEFAULT NULL COMMENT '营业执照注册号/证书编号',
  `license_term` int(11) DEFAULT NULL COMMENT '证件有效期（1 长期有效；2 固定时限）',
  `license_term_time` date DEFAULT NULL COMMENT '证件有效期至',
  `legal_front_pic` varchar(2000) DEFAULT NULL COMMENT '法人身份证正面照',
  `legal_back_pic` varchar(2000) DEFAULT NULL COMMENT '法人身份证背面照',
  `verify_status` int(11) NOT NULL COMMENT '审核状态（0 未审核，1 审核成功，2 审核失败）',
  `verify_time` datetime DEFAULT NULL COMMENT '审核时间',
  `verify_note` varchar(3000) DEFAULT NULL COMMENT '审核备注',
  `note` varchar(3000) DEFAULT NULL COMMENT '备注',
  `create_time` datetime NOT NULL COMMENT '添加时间',
  `create_by` varchar(200) NOT NULL COMMENT '添加人',
  `modify_time` datetime DEFAULT NULL COMMENT '修改时间',
  `modify_by` varchar(200) DEFAULT NULL COMMENT '修改人',
  PRIMARY KEY (`org_apply_id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='机构入驻申请表';

