  `organization_name` varchar(100) DEFAULT NULL COMMENT '机构名称',
  `org_code` varchar(50) DEFAULT NULL COMMENT '入驻者组织编码',
  `type` int(11) DEFAULT NULL COMMENT '用户类型( 1.本部；2.教师；3第三方机构；4租户)',
  `user_name` varchar(50) DEFAULT NULL COMMENT '负责人账号',
  `real_name` varchar(50) DEFAULT NULL COMMENT '负责人真实姓名',
  `phone` varchar(30) DEFAULT NULL COMMENT '手机号',
  `email` varchar(50) DEFAULT NULL COMMENT '邮箱',
  `status` int(11) NOT NULL COMMENT '状态（1. 正常 ，2 停用）',
  `verify_status` int(11) NOT NULL COMMENT '审核状态（0 未审核，1 审核成功，2 审核失败）',
  `verify_time` datetime DEFAULT NULL COMMENT '审核时间',
  `verify_note` varchar(3000) DEFAULT NULL COMMENT '审核备注',
  `is_deleted` tinyint(1) DEFAULT NULL COMMENT '是否删除',
  `create_time` datetime NOT NULL COMMENT '添加时间',
  `create_by` varchar(50) NOT NULL COMMENT '添加人',
  `modify_time` datetime NOT NULL COMMENT '修改时间',
  `modify_by` varchar(50) NOT NULL COMMENT '修改人',
  `organization_logo` varchar(2000) DEFAULT NULL COMMENT '机构Logo图标',
  `web_pic` varchar(2000) DEFAULT NULL COMMENT '机构网站图标',
  `cc_id` varchar(32) DEFAULT NULL COMMENT '主营类目',
  PRIMARY KEY (`organization_id`) USING BTREE,
  KEY `AK_Key_2` (`org_code`) USING BTREE
